import React, { useState } from 'react';
import { Modal } from '../../context/Modal';
import EditQuestionForm from './editQuestionForm';
import { useHistory } from 'react-router-dom';



function EditQuestionFormModal({ question }) {
    console.log('edit q prop', question)
    const [showModal, setShowModal] = useState(false);
    const history = useHistory()

    const onSub = (e) => {
        e.preventDefault()
        setShowModal(true)
        history.push(`edit-question/${question.id}`)
    }

    return (
        <>
            <button onClick={setShowModal(true)}>Edit</button>
            {
                showModal && (
                    <Modal onClose={() => setShowModal(false)}>
                        <EditQuestionForm setShowModal={setShowModal} question={question} />
                    </Modal>
                )
            }
        </>
    );
}

export default EditQuestionFormModal;
