import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useHistory } from 'react-router-dom'
import { createNewQuestion, getAllQuestions } from '../../store/questions'


function QuestionForm({ setShowModal }) {
    const dispatch = useDispatch()
    const history = useHistory()
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [image, setImage] = useState('')
    const [error, setError] = useState([])

    useEffect(() => {
        let err = []

        if (body.length > 750) err.push('Body must be less that 1500 characters')
        if (body.length < 6) err.push('Body must be greater than 5 characters')
        if (title.length > 100) err.push('Title must be less than 100 characters')
        if (title.length < 4) err.push('Title must be greater than 4 characters')
        setError(err)

    }, [body])

    const handleSubmit = async (e) => {
        e.preventDefault()

        let newQuestion = {
            title,
            body,
            image
        }
        dispatch(createNewQuestion(newQuestion)).then(() => dispatch(getAllQuestions()))
        setShowModal(false)
        return history.push('/')
    }

    return (
        <form onSubmit={handleSubmit}>
            {error.length && (
                <ul className="error-map">{error.map((err, i) => (
                    <li key={i}>{err}</li>
                ))}
                </ul>
            )}
            <div>
                <input required minlength='5' maxlength='101' type='text' placeholder='Question Title' value={title} onChange={(e) => setTitle(e.target.value)} />
            </div>
            <div>
                <textarea required minlength='5' maxlength='751' type='text' placeholder='Start your question with "What", "How", "Why", etc' value={body} onChange={(e) => setBody(e.target.value)} />
            </div>
            <div>
                <button disabled={!!error.length} type='submit'>Add Question</button>
            </div>
        </form>
    )
}

export default QuestionForm
