//################### Actions   ###########################
const LOAD_RELATED = 'question/LOAD_RELATED'


//################## Action Creators ######################
const loadRelated = (rQuestions) => {
    return {
        'type': LOAD_RELATED,
        rQuestions
    }
}

//######################## Thunks ############################


export const getRelatedQuestions = () => async dispatch => {
    const response = await fetch(`/api/questions/relatedQ`)

    if (response.ok) {
        const rQuestions = await response.json()
        dispatch(loadRelated(rQuestions))
        return rQuestions
    }
}

let initialState = {}
//######################## Reducer ##########################

const questionsReducer = (state = initialState, action) => {
    let newState = {}
    switch (action.type) {
        case LOAD_RELATED: {
            newState = { ...action.related }
            return newState
        }
        // case EDIT_QUESTION: {
        //     newState = { ...state }
        //     newState = { ...action.questions }

        // }
        // case DELETE_QUESTION: {
        //     delete newState[action.deleted]
        //     newState = { ...newState }
        //     return newState
        // }
        default:
            return state
    }
}


export default relatedReducer
